dfx start --background --clean
export PRINCIPAL=$(dfx identity get-principal)
yarn install
dfx canister create --all
dfx build 

dfx canister install fishverse_ext --argument="(principal \"$PRINCIPAL\")"
dfx canister call fishverse_ext initBaseTokenTypes "(\"\")"

dfx deploy icrc1_ledger --argument "(record {
  token_symbol = \"FVS\";
  token_name = \"FishVerse Metaverse Token\";
  minting_account = record { owner = principal \"$PRINCIPAL\" };
  transfer_fee = 10_000;
  metadata = vec { record { \"icrc1:logo\"; variant { Text = \"data:image/png;base64,\"}}};
  initial_balances = vec {};
  archive_options = record {
    num_blocks_to_archive = 2000;
    trigger_threshold = 1000;
    controller_id = principal \"$PRINCIPAL\";
  };
},)"

dfx deploy www